import React, { useState } from 'react';

export function PasswordProtectedPopup({ isOpen, onClose, correctPassword }) {
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        if (password === correctPassword) {
            // Password is correct, proceed to show the case study
            onClose(); // Close the popup
            window.open('/path-to-your-case-study', '_blank'); // Open the case study in a new tab
        } else {
            setError('Incorrect password. Please try again.');
        }
    };

    if (!isOpen) return null; // If popup is not open, render nothing

    useEffect(() => {
        if (props.trigger) {
            // Disable scrolling
            document.body.style.overflow = 'hidden';
        } else {
            // Enable scrolling
            document.body.style.overflow = 'auto';
        }
        return () => {
            document.body.style.overflow = 'auto';
        };
    }, [props.trigger]);

    return (
        <div className="popup-overlay">
            <div className="popup-content">
                <button className="close-btn" onClick={onClose}>
                    <img src='./img/backarrow.svg' alt='back arrow'/>
                </button>
                <h3 className='password-title'>Enter Password</h3>
                <form onSubmit={handleSubmit}>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter password"
                    />
                    {error && <p className="error-message">{error}</p>}
                    <button type="submit" className='password-submit'>Submit</button>
                </form>
            </div>
        </div>
    );
}
